import { useState } from 'react';
import './App.css';

function App() {
const [task, setTask] = useState('');

const handleInput = ({ target }) => {
  const { value } = target;
  setTask(value);
}
  return (
    <div className="App">
      <h1>Todo List</h1>
      <input
        type="text"
        onChange={ handleInput }
        name="task"
        value={ task }
      />
    </div>
  );
}

export default App;
