import { useState } from 'react';
import './App.css';

function App() {
const [task, setTask] = useState('');
const [myTasks, setMyTasks] = useState([]);

const handleInput = ({ target }) => {
  const { value } = target;
  setTask(value);
}

const handleClick = () => {
 return task.length !== 0 ? setMyTasks(task) : task;
}
  return (
    <div className="App">
      <h1>Todo List</h1>
      <input
        type="text"
        onChange={ handleInput }
        name="task"
        value={ task }
      />
      <button
        type="button"
        onClick={ handleClick }
      >
        Add Task
      </button>
      <ul>
        {myTasks.length !== 0 && myTasks.map((x) => (
          <li>{ x }</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
