import './App.css';
import { useEffect, useState } from 'react';

function App() {
  const [number, setNumber] = useState(0);

  useEffect(() => randomNumber());

  const randomNumber = () => {
    const SECONDS = 1000;
    let randInt = 0;
    setInterval(() => {
      randInt = Math.floor(Math.random() * 100) + 1;
      setNumber(randInt);
    }, SECONDS);
  }
  return (
    <div className="App">
      <p>{number}</p>
    </div>
  );
}

export default App;
